import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const StudentPage = () => {
  const [fileData, setFileData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:3100/api/getFileData', {
          headers: {
            Authorization: localStorage.getItem("token")
          }
        });
        setFileData(res.data);
      } catch (error) {
        console.error('Error fetching file data:', error);
      }
    };

    fetchData();
  }, []);

  const handleBack = () => {
    navigate('/Etudiant');
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Page Etudiant</h2>
      {fileData.length > 0 ? (
        <div>
          <h3>Images téléversées :</h3>
          {fileData.map(file => (
            <div key={file.id} style={{ marginBottom: '20px' }}>
              <p>Nom du fichier: {file.filename}</p>
              <img
                src={`http://localhost:3100/${file.path}`}
                alt="Image téléversée"
                className="minimized-image"
              />
              <hr />
            </div>
          ))}
        </div>
      ) : (
        <p>Aucune image disponible.</p>
      )}
      <button onClick={handleBack} style={{ marginTop: '20px', padding: '10px 20px', cursor: 'pointer' }}>Retour</button>
    </div>
  );
};

export default StudentPage;
