import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AdminEmploiTemps = () => {
  const [emploisTemps, setEmploisTemps] = useState([]);
  const [nouvelEmploi, setNouvelEmploi] = useState({ jour: '', heure: '', matiere: '', professeur: '' });
  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setNouvelEmploi(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const ajouterEmploi = () => {
    setEmploisTemps(prevEmplois => [...prevEmplois, nouvelEmploi]);
    setNouvelEmploi({ jour: '', heure: '', matiere: '', professeur: '' });
  };

  const handleBack = () => {
    navigate('/Admin_aff');
  };

  return (
    <div>
      <h2>Créer un emploi du temps</h2>
      <table>
        <thead>
          <tr>
            <th>Jour</th>
            <th>Heure</th>
            <th>Matière</th>
            <th>Professeur</th>
          </tr>
        </thead>
        <tbody>
          {emploisTemps.map((emploi, index) => (
            <tr key={index}>
              <td>{emploi.jour}</td>
              <td>{emploi.heure}</td>
              <td>{emploi.matiere}</td>
              <td>{emploi.professeur}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div>
        <label>Jour:</label>
        <input type="text" name="jour" value={nouvelEmploi.jour} onChange={handleChange} />
      </div>
      <div>
        <label>Heure:</label>
        <input type="text" name="heure" value={nouvelEmploi.heure} onChange={handleChange} />
      </div>
      <div>
        <label>Matière:</label>
        <input type="text" name="matiere" value={nouvelEmploi.matiere} onChange={handleChange} />
      </div>
      <div>
        <label>Professeur:</label>
        <input type="text" name="professeur" value={nouvelEmploi.professeur} onChange={handleChange} />
      </div>
      <button onClick={ajouterEmploi}>Ajouter Emploi</button>
      <button onClick={handleBack}>Retour</button>
    </div>
  );
};

export default AdminEmploiTemps;
