import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EtudiantPage = () => {
  const [fileData, setFileData] = useState([]);
  const [selectedFileId, setSelectedFileId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchFileData();
  }, []);

  const fetchFileData = () => {
    const token = localStorage.getItem('token'); // Retrieve the authentication token from localStorage

    axios
      .get('http://localhost:3100/api/getFileData', {
        headers: {
          Authorization: token // Send the token in the request headers
        }
      })
      .then(res => {
        setFileData(res.data);
        console.log("res.data:",res.data);
      })
      .catch(error => {
        console.error('Error fetching file data:', error);
      });
  };

  const handleSelect = (fileId) => {
    setSelectedFileId(fileId);
  };

  const handleDelete = (fileId) => {
    const token = localStorage.getItem('token'); // Retrieve the authentication token from localStorage

    axios
      .delete(`http://localhost:3100/api/deleteFile/${fileId}`, {
        headers: {
          Authorization: token // Send the token in the request headers
        }
      })
      .then(res => {
        console.log("File deleted:", res.data);
        fetchFileData(); // Refresh the file data after deletion
      })
      .catch(error => {
        console.error('Error deleting file:', error);
      });
  };

  const handleBack = () => {
    navigate('/admin_upl');
  };

  return (
    <div>
      <h2>Page admin</h2>
      {fileData.length > 0 ? (
        <div>
          <h3>Images téléversées :</h3>
          {fileData.map(file => (
            <div key={file.id} style={{ border: selectedFileId === file.id ? '2px solid blue' : 'none' }}>
              <p>Nom du fichier: {file.filename}</p>
              <img src={`http://localhost:3100/${file.path}`} alt="Image téléversée" style={{ maxWidth: '100px', height: '100px' }} />
              <button onClick={() => handleSelect(file.path)}>Sélectionner</button>
              <button onClick={() => handleDelete(file.path)}>Supprimer</button>
              <hr />
            </div>
          ))}
        </div>
      ) : (
        <p>Aucune image disponible.</p>
      )}
      <button onClick={handleBack}>Retour</button>
    </div>
  );
};

export default EtudiantPage;
