import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EtudiantPage = () => {
  const [fileData, setFileData] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchFileData();
  }, []);

  const fetchFileData = () => {
    const token = localStorage.getItem('token'); // Retrieve the authentication token from localStorage

    axios
      .get('http://localhost:3100/api/getFileData', {
        headers: {
          Authorization: token // Send the token in the request headers
        }
      })
      .then(res => {
        setFileData(res.data);
        console.log(res.data);
      })
      .catch(error => {
        console.error('Error fetching file data:', error);
      });
  };

  const handleBack = () => {
    navigate('/');
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.heading}>Page Etudiant</h2>
      {fileData.length > 0 ? (
        <div>
          <h3 style={styles.subHeading}>Images téléversées :</h3>
          {fileData.map(file => (
            <div key={file.id} style={styles.fileItem}>
              <p style={styles.fileName}>Nom du fichier: {file.filename}</p>
              <img src={`http://localhost:3100/${file.path}`} alt="Image téléversée" style={styles.image} />
              <hr style={styles.hr} />
            </div>
          ))}
        </div>
      ) : (
        <p style={styles.noImage}>Aucune image disponible.</p>
      )}
      <button style={styles.button} onClick={handleBack}>Retour</button>
    </div>
  );
};

const styles = {
  container: {
    textAlign: 'center',
    margin: '20px auto',
    maxWidth: '800px'
  },
  heading: {
    fontSize: '24px',
    marginBottom: '20px',
    color: '#333'
  },
  subHeading: {
    fontSize: '20px',
    marginBottom: '15px',
    color: '#555'
  },
  fileItem: {
    marginBottom: '20px'
  },
  fileName: {
    fontSize: '18px',
    marginBottom: '10px',
    color: '#444'
  },
  image: {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '5px'
  },
  hr: {
    borderTop: '1px solid #ccc',
    width: '100%'
  },
  noImage: {
    fontSize: '18px',
    marginTop: '20px',
    color: '#555'
  },
  button: {
    display: 'block',
    margin: '20px auto',
    padding: '10px 20px',
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    fontSize: '18px',
    cursor: 'pointer',
    transition: 'background-color 0.3s ease'
  }
};

export default EtudiantPage;
